/* -- AIRLINES-1 --

Remove from the flights database all flights except those to or from the airport with code AKI. */
DELETE FROM Flights WHERE NOT(SourceAirport = 'AKI' OR DestAirport = 'AKI');

/* -- AIRLINES-2 -- 

For all flights NOT operated by Continental, AirTran or Virgin, increase the flight number by 2000.*/ 
UPDATE Flights
SET FlightNo = FlightNo + 2000
WHERE Airline = 7 OR Airline = 10 OR Airline = 12;

/* -- AIRLINES-3 -- 

Normalize all tables in the AIRLINES dataset to Third Normal Form (3NF).
ALTER TABLE flights ADD leg_room INTEGER;
UPDATE lab_assignments SET bad_sql_jokes = bad_sql_jokes + 1 WHERE lab_no = 3;*/

DROP TABLE if exists 3nf_Flights;
DROP TABLE if exists 3nf_Airlines;
DROP TABLE if exists 3nf_Airports;
DROP TABLE if exists 3nf_Airports_Location;

CREATE TABLE 3nf_Airlines(
   Id INTEGER primary key,
   Airline varchar(250),
   Abbreviation varchar(250),
   Country varchar(20) 
);

CREATE TABLE 3nf_Airports_Location(
	City varchar(250) primary key,
   	Country varchar(250),
   	CountryAbbrev varchar(10)
);

CREATE TABLE 3nf_Airports(
	City varchar(250),
   	AirportCode varchar(10) primary key,
   	AirportName varchar(250),

   	foreign key (City) references 3nf_Airports_Location (City)
);

CREATE TABLE 3nf_Flights(
   Airline INTEGER,
   FlightNo INTEGER,
   SourceAirport varchar(5),
   DestAirport varchar(5), 

   primary key (Airline, FlightNo),

   foreign key (Airline) references 3nf_Airlines (Id),
   foreign key (SourceAirport) references 3nf_Airports (AirportCode),
   foreign key (DestAirport) references 3nf_Airports (AirportCode)
);

