/* -- BAKERY-1 -- 

Reduce the prices of Lemon Cake and Napoleon Cake by $2.*/
UPDATE Goods
SET Price = Price - 2.00
WHERE (Flavor = 'Lemon' AND Food = 'Cake') OR (Flavor = 'Napoleon' AND Food = 'Cake');

/* -- BAKERY-2 -- 

Increase by 15% the price of all Apricot or 
Chocolate flavored items with a current price below $5.95.*/
UPDATE Goods
SET Price = Price + (Price * 0.15)
WHERE (Flavor = 'Apricot' AND Price < 5.95) OR (Flavor = 'Chocolate' AND Price < 5.95);

